    this.Object = [];
    this.Drawing = [];
    this.Update = [];
    this.Hardware = [];
    this.CommentBoard = [];
    this.boardName = "";
    this.drawNumber = "";
    this.index = 0;
    this.i = this.index;
    this.isButtonVis = true;
    this.isButtonVi = false;
    this.isButtonVisible = true;
    this.visible = false;
    this.isVisible = false;
    this.isVis = false;
    this.data = [];
    this.Variableopen = false;
    this.variables = [{
      objectName: {
