import { Component, Input } from '@angular/core';
import { SearchDialogComponent } from '../search-dialog/search-dialog.component';
import { MatDialog } from '@angular/material/dialog'
import { NewBoardComponent } from '../new-board/new-board.component';
import { UpdateProjectComponent } from '../update-project/update-project.component';
import { UpdateBoardComponent } from '../update-board/update-board.component';
import { IVariableObject, IProjectObject, IBoardObject, DataService, IDataObject } from '../data.service';
import { CdkDragDrop, CdkDrag, CdkDropList, moveItemInArray } from '@angular/cdk/drag-drop';
import { DragDropModule } from '@angular/cdk/drag-drop';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
})
export class HomeComponent {
  @Input() name = "";
  @Input() Job= "";
  @Input() Manufacturer= "";
  @Input() Aircraft= "";
  @Input() Object: string[] = [];
  @Input() Drawing: string[] = [];
  @Input() Update: string[] = [];
  @Input() Hardware: string[] = [];
  @Input() CommentBoard: string[] = [];
  @Input() boardName: string = "";
  @Input() drawNumber: string = "";
  index: number = 0;
  i: number = this.index;
  isButtonVis: boolean = true;
  isButtonVi: boolean = false;
  isButtonVisible: boolean = true;
  visible: boolean = false;
  isVisible: boolean = false;

  isVis: boolean = false;
  data: IDataObject[] = [];
  Variableopen: boolean = false;
  variables: IVariableObject[] = [
    {
      objectName: {
        label: 'Object Name',
        model: '',
        placeholder: '',
        disabled: true
      },
      drawingNumber: {
        label: 'Drawing Number',
        model: '',
        placeholder: '',
        disabled: true
      },
      direction: {
        label: 'Manufacturer',
        model: '',
        disabled: true
      },
      dataType: {
        label: 'Aircraft Name',
        model: '',
        placeholder: '',
        disabled: true
      },
      size: {
        label: 'Size',
        model: '',
        placeholder:'',
        disabled: true
      }
    }
  ];
  projectFields: IProjectObject[] = [
    {
      username: {
        label: 'Username',
        model: '',
        placeholder: '',
        disabled: false
      },
      Job: {
        label: 'Job #',
        model: '',
        placeholder: '',
        disabled: false
      },
      Manufacturer: {
        label: 'Manufacturer',
        model: '',
        placeholder: '',
        disabled: false
      },
      Aircraft: {
        label: 'Aircraft Name',
        model: '',
        placeholder: '',
        disabled: false
      }
    }
  ];
  boardFields: IBoardObject[] = [];

  // the board we're working with.
  board = this.dataService.data.boards[this.dataService.data.current_board];

  constructor(
    public dialog: MatDialog,
    public dataService: DataService
  ) {}

  saveData(): void {
    if (this.projectFields.length > 0) {
      // capture data
      const userName = this.projectFields[0].username.model;
      const jobNumber = this.projectFields[0].Job;
      const manufacturer = this.projectFields[0].Manufacturer;
      const aircraft = this.projectFields[0].Aircraft;

      this.projectFields.forEach((x)=>console.log(x));

      const jsonData: string = JSON.stringify({ userName });
      localStorage.setItem('data', jsonData);
      // display saved data
      console.log('Saved User Name:', userName);
      console.log('Saved Job:', jobNumber);
      console.log('Saved Manufacturer:', manufacturer);
      console.log('Saved Aircraft:', aircraft);
    }
  }



  shouldRun: boolean  = true;
  isSidenavOpened: boolean = false;

  toggleSidenav(): void {
    this.isSidenavOpened = !this.isSidenavOpened;
  }

  openDialog(): void {
    const dialogRef = this.dialog.open(SearchDialogComponent, {
      width: '900px'
    });

    dialogRef.afterClosed().subscribe((result: any) => {

    });
  }

  openAddNewBoard(): void {
    console.log('BJ was here - open add new board.');
    const dialogRef = this.dialog.open(NewBoardComponent, {
        data: (JSON.parse(JSON.stringify(this.boardFields[this.i])))
      });

    dialogRef.afterClosed().subscribe((result: any) => {
      console.log("afterclosed" , result)
      if(result){
        this.boardFields[this.i] = result
      }
      this.boardFields.push(result);
      console.log(result)
    });
  }

  updateProject(): void {
    const dialogRef = this.dialog.open(UpdateProjectComponent, {
      data: (JSON.parse(JSON.stringify(this.projectFields[0])))
    });
    dialogRef.afterClosed().subscribe(result => {
      if(result.username){
      this.projectFields[0] = result
      }

    })

  }

  updateBoard(): void {
      // capture data
      const boardName = this.boardFields[0].boardName;
      const drawingNumber = this.boardFields[0].drawNumber;
      const updateRate = this.boardFields[0].updateRate;
      const hardwareVersion = this.boardFields[0].hardware;
      const comment = this.boardFields[0].comment;

      this.boardFields.forEach((x)=>console.log(x));

    const dialogRef = this.dialog.open(UpdateBoardComponent, {
      data: (JSON.parse(JSON.stringify(this.boardFields[0])))
    });
    dialogRef.afterClosed().subscribe(result => {
      if(result.boardName){
      this.boardFields[0] = result
      }

      console.log('Saved Board Name"', boardName);
      console.log('Saved Drawing Number:', drawingNumber);
      console.log('Saved Update Rate:', updateRate);
      console.log('Saved Hardware Version:', hardwareVersion);
      console.log('Saved Comment:', comment);
    })
  }

  createBoard(): void {
    let index = this.dataService.data.boards.length;
    this.boardFields.push({
      index: index,
      boardName: {
        label: 'Board Name',
        model: '',
        placeholder: '',
        disabled: false
      },
      drawNumber: {
        label: 'Drawing Number',
        model: '',
        placeholder: '',
        disabled: false
      },
      updateRate: {
        label: 'Update Rate',
        model: '',
        placeholder: '',
        disabled: false
      },
      hardware: {
        label: 'Hardware Version',
        model: '',
        placeholder: '',
        disabled: false
      },
      comment: {
        label: 'Comment',
        model: '',
        placeholder: '',
        disabled: false
      },
      variables: [],
      disabled: false
    });
    this.disableProjectFields();
    this.isButtonVis = false;
    this.isVisible = true;
    this.saveData();
  }

  createVariable(): void {
    this.variables.push({
      objectName: { 
        label: 'Object Name',
        model: '',
        placeholder: '',
        disabled: false 
      },
      drawingNumber: {
        label: 'Drawing Number', 
        model: '', 
        placeholder: '',
        disabled: false
      },
      direction: {
        label: 'Direction',
        model: '',
        disabled: false
      },
      dataType: { 
        label: 'Data Type', 
        model: '', 
        placeholder: '',
        disabled: false
      },
      size: { 
        label: 'Size', 
        model: '', 
        placeholder: '',
        disabled: false
      }
    });

    this.disableboardFields();
    this.isButtonVisible = false;
    this.visible = true;
    this.isVis = true;
    this.Variableopen = true;
  }

  addLines(): void {
    this.dataService.data.boards[this.dataService.data.current_board].variables.push({
      objectName: { 
        label: 'Object Name',
        model: '',
        placeholder: '',
        disabled: false 
      },
      drawingNumber: {
        label: 'Drawing Number', 
        model: '', 
        placeholder: '',
        disabled: false
      },
      direction: {
        label: 'Direction',
        model: '',
        disabled: false
      },
      dataType: { 
        label: 'Data Type', 
        model: '', 
        placeholder: '',
        disabled: false
      },
      size: { 
        label: 'Size', 
        model: '', 
        placeholder: '',
        disabled: false
      }
  });
}
  reveal(): void {
    this.disableboardFields();
    this.isButtonVisible = false;
    this.visible = true;
    this.isVis = true;
    this.Variableopen = true;
  };
  // called when "clear variables" button is pressed
  clearFields(): void {
    console.log('BJ was here - clear fields.');
    this. dataService.data.boards[this.dataService.data.current_board].variables.forEach((variable) => {
      variable.objectName.model = '';
      variable.drawingNumber.model = '';
      variable.direction.model = null;
      variable.dataType.model = '';
      variable.size.model = '';
      this.dataService.data.boards[this.dataService.data.current_board].variables.splice(1);
    });
  }



  removeVariable(index: number): void {
    this.dataService.data.boards[this.dataService.data.current_board].variables.splice(index, 1);
  }

  disableProjectFields(): void {
    for (let field of this.projectFields) {
      field.username.disabled = true;
      field.Job.disabled = true;
      field.Manufacturer.disabled = true;
      field.Aircraft.disabled = true;
    }
  }

  disableboardFields(): void {
      this.boardFields[this.i].boardName.disabled = true;
      this.boardFields[this.i].drawNumber.disabled = true;
      this.boardFields[this.i].updateRate.disabled = true;
      this.boardFields[this.i].hardware.disabled = true;
  }

  switchBoard(boardindex: number) {
    console.log('BJ was here - switch board.');
    console.log(boardindex);
    this.dataService.data.current_board = boardindex;

    this.board = this.dataService.data.boards[boardindex];
    console.log('this board is : ', this.board);
    console.log('this board\'s parent is : ', this.boardFields);
    // close the side nav after board selection?
  }
  openNav(){
    console.log("Board Side Panel opened.");// ToDo(BJ): something a bit more meaningful should be here. function name?
    let bar = document.getElementById("bar")
    if (bar){
      bar.style.width = "250px"
      document.getElementById("main")!.style.marginLeft ="250px";
    }

    // 
 } 
  closeNav(){
    console.log("Board Side Panel closed.");
    document.getElementById("bar")!.style.width = "0"
    document.getElementById("main")!.style.marginLeft = "0";
  }
}
<div id="bar" class="sidebar">
    <a href="javascript:void(0)" class="closebtn" (click)="closeNav()"><mat-icon>close</mat-icon></a>
    <ul *ngIf="board.boardName.model">
        <li class="pb-2" *ngFor="let board of dataService.data.boards; let i = index">
            <div>{{board.boardName.model}}</div>
            <button class="btn btn-light" href="#" (click)="switchBoard(i)" [disabled]="board.disabled">{{ board.boardName.model }}</button>
        </li>
        <button class="btn btn-success" *ngIf="board.boardName.model" (click)="openAddNewBoard()">Add Board</button>
    </ul>
    <ul><button class="btn btn-dark" (click)="openDialog()">Search</button></ul>
</div>
<div id="main">
    <div class="container">
        <button class=" btn btn-dark home btn-lg" (click)="openNav()">☰</button>
        <div class="row">
            <div class="col">
                <h1>Project Information</h1>
            </div>
        </div>
        <div class="row" *ngFor="let field of projectFields">
            <div class="col-auto">
                <mat-form-field appearance="fill">
                    <mat-label>{{ field.username.label }}</mat-label>
                    <input matInput [(ngModel)]="field.username.model" [ngModelOptions]="{standalone: true}" objectName="field.objectName.model" placeholder="" [disabled]="field.username.disabled" >
                    <button mat-button *ngIf="field.username.model" matSuffix mat-icon-button aria-label="Clear" (click)="field.username.model = ''" [disabled]="field.username.disabled" [hidden]="!isButtonVis">
                        <mat-icon>close</mat-icon>
                    </button>
                </mat-form-field>
            </div>
            <div class="col-auto">
                <mat-form-field appearance="fill">
                    <mat-label>{{ field.Job.label }}</mat-label>
                    <input matInput [(ngModel)]="field.Job.model" [ngModelOptions]="{standalone: true}" Job="field.Job.model" placeholder="" [disabled]="field.Job.disabled">
                    <button mat-button *ngIf="field.Job.model" matSuffix mat-icon-button aria-label="Clear" (click)="field.Job.model = ''" [disabled]="field.Job.disabled" [hidden]="!isButtonVis">
                        <mat-icon>close</mat-icon>
                    </button>
                </mat-form-field>
            </div>
            <div class="col-auto">
                <mat-form-field appearance="fill">
                    <mat-label>{{ field.Manufacturer.label }}</mat-label>
                    <input matInput [(ngModel)]="field.Manufacturer.model" [ngModelOptions]="{standalone: true}" Manufacturer="field.Manufacturer.model" placeholder="" [disabled]="field.Manufacturer.disabled">
                    <button mat-button *ngIf="field.Manufacturer.model" matSuffix mat-icon-button aria-label="Clear" (click)="field.Manufacturer.model = ''" [disabled]="field.Manufacturer.disabled" [hidden]="!isButtonVis">
                        <mat-icon>close</mat-icon>
                    </button>
                </mat-form-field>
            </div>
            <div class="col-auto">
                <mat-form-field appearance="fill">
                    <mat-label>{{ field.Aircraft.label }}</mat-label>
                    <input  matInput [(ngModel)]="field.Aircraft.model" [ngModelOptions]="{standalone: true}" Aircraft="field.Aircraft.model" placeholder=""[disabled]="field.Aircraft.disabled">
                    <button mat-button *ngIf="field.Aircraft.model" [disabled]="field.Aircraft.disabled" matSuffix mat-icon-button aria-label="Clear" (click)="field.Aircraft.model = ''" [hidden]="!isButtonVis">
                        <mat-icon>close</mat-icon>
                    </button>
                </mat-form-field>
            </div>
            <div class="col">
                <button class="btn btn-light float-end" mat-button matSuffix mat-icon-button (click)="updateProject()" [hidden]="!isVisible">
                    <mat-icon>edit</mat-icon>
                </button>
            </div>
        </div>
        <div class="row">
            <mat-divider></mat-divider>
        </div>
        <p></p>
        <div class="row">
            <div class="col-3">
                <button class="btn btn-secondary" mat-button (click)="createBoard()" [hidden]="!isButtonVis">Save Details</button>
            </div>
        </div>
        <p></p>
        <div class="row" *ngIf="isVisible"  >
            <div *ngFor="let board of dataService.data.boards"></div>
            <h2>Board Information</h2>
        </div>
        <div class="row" *ngIf="isVisible">
            <div class="col">
                <mat-form-field appearance="fill" >
                    <mat-label>{{ board.boardName.label }}</mat-label>
                    <input matInput [(ngModel)]="board.boardName.model" placeholder="">
                    <button mat-button *ngIf="board.boardName.model" matSuffix mat-icon-button aria-label="Clear" (click)="board.boardName.model = ''" [hidden]="!isButtonVisible" [disabled]="board.boardName.disabled">
                        <mat-icon>close</mat-icon>
                    </button>
                    </mat-form-field>
            </div>
            <div class="col">
                <mat-form-field appearance="fill">
                    <mat-label>{{ board.drawNumber.label }}</mat-label>
                    <input matInput [(ngModel)]="board.drawNumber.model" [ngModelOptions]="{standalone: true}" drawNumber="board.drawNumber.model" placeholder="" >
                    <button mat-button *ngIf="board.drawNumber.model" matSuffix mat-icon-button aria-label="Clear" (click)="board.drawNumber.model = ''" [hidden]="!isButtonVisible" [disabled]="board.drawNumber.disabled">
                        <mat-icon>close</mat-icon>
                    </button>
                    </mat-form-field>
            </div>
            <div class="col">
                <mat-form-field appearance="fill">
                    <mat-label>{{ board.updateRate.label }}</mat-label>
                    <input matInput [(ngModel)]="board.updateRate.model" [ngModelOptions]="{standalone: true}" updateRate="board.updateRate.model" placeholder="" >
                    <button mat-button *ngIf="board.updateRate.model" matSuffix mat-icon-button aria-label="Clear" (click)="board.updateRate.model = ''" [hidden]="!isButtonVisible" [disabled]="board.updateRate.disabled">
                        <mat-icon>close</mat-icon>
                    </button>
                </mat-form-field>
            </div>
            <div class="col">  
                <mat-form-field appearance="fill">
                    <mat-label>{{ board.hardware.label }}</mat-label>
                    <input matInput [(ngModel)]="board.hardware.model" [ngModelOptions]="{standalone: true}" hardware="board.hardware.model" placeholder="" >
                    <button mat-button *ngIf="board.hardware.model" matSuffix mat-icon-button aria-label="Clear" (click)="board.hardware.model = ''" [hidden]="!isButtonVisible" [disabled]="board.hardware.disabled">
                        <mat-icon>close</mat-icon>
                    </button>
                </mat-form-field>
            </div>
            <div class="col">
                <mat-form-field>
                    <mat-label>Comment</mat-label>
                    <textarea matInput></textarea>
                </mat-form-field>
            <div class="col">
                <button class="btn btn-light float-end" mat-button matSuffix mat-icon-button (click)="updateBoard()" [hidden]="!isVis">
                    <mat-icon>edit</mat-icon>
                </button>
            </div>
        </div>
        <div class="row" *ngIf="isVisible">
            <mat-divider></mat-divider>
        </div>
        <p></p>
        <div class="row" *ngIf="isVisible">
            <div class="col-3">
                <button class="btn btn-secondary" mat-button (click)="createVariable()" [hidden]="!isButtonVisible">Save Details</button>
            </div>
        </div>
        <p></p>
        <div class="row" *ngIf="Variableopen">
            <h3>Variable Information</h3>
        </div>
        <div>
        <div class="row" *ngFor="let variable of dataService.data.boards[dataService.data.current_board].variables; let i = index">
            <div class="col" *ngIf="Variableopen">
                <mat-form-field appearance="fill" >
                    <mat-label>{{ variable.objectName.label }}</mat-label>
                    <input matInput [(ngModel)]="variable.objectName.model" [ngModelOptions]="{standalone: true}" objectName="field.objectName.model" placeholder="" [disabled]="variable.objectName.disabled">
                    <button mat-button *ngIf="variable.objectName.model" matSuffix mat-icon-button aria-label="Clear" (click)="variable.objectName.model = ''" [disabled]="variable.objectName.disabled">
                        <mat-icon>close</mat-icon>
                    </button>
                </mat-form-field>
            </div>
            <div class="col" *ngIf="Variableopen">
                <mat-form-field appearance="fill">
                    <mat-label>{{ variable.drawingNumber.label }}</mat-label>
                    <input matInput [(ngModel)]="variable.drawingNumber.model" [ngModelOptions]="{standalone: true}" drawingNumber="field.drawingNumber.model" placeholder="" [disabled]="variable.drawingNumber.disabled">
                    <button mat-button *ngIf="variable.drawingNumber.model" matSuffix mat-icon-button aria-label="Clear" (click)="variable.drawingNumber.model = ''"[disabled]="variable.drawingNumber.disabled">
                        <mat-icon>close</mat-icon>
                    </button>
                </mat-form-field>
            </div>
            <div class="col" *ngIf="Variableopen">
                <mat-form-field appearance="fill">
                    <mat-label >{{variable.direction.label}}</mat-label>
                    <mat-select>
                        <mat-option >None</mat-option>
                        <mat-option value="one">Input</mat-option>
                        <mat-option value="Two">Output</mat-option>
                    </mat-select>
                </mat-form-field>
            </div>
            <div class="col" *ngIf="Variableopen">
                <mat-form-field appearance="fill">
                    <mat-label>{{ variable.dataType.label }}</mat-label>
                    <input matInput [(ngModel)]="variable.dataType.model" [ngModelOptions]="{standalone: true}" dataType="field.dataType.model" placeholder="" [disabled]="variable.dataType.disabled">
                    <button mat-button *ngIf="variable.dataType.model" matSuffix mat-icon-button aria-label="Clear" (click)="variable.dataType.model = ''"[disabled]="variable.dataType.disabled">
                        <mat-icon>close</mat-icon>
                    </button>
                </mat-form-field>
            </div>
            <div class="col" *ngIf="Variableopen">
                <mat-form-field appearance="fill">
                    <mat-label>{{ variable.size.label }}</mat-label>
                    <input matInput [(ngModel)]="variable.size.model" [ngModelOptions]="{standalone: true}" size="field.size.model" placeholder="" [disabled]="variable.size.disabled">
                    <button mat-button *ngIf="variable.size.model" matSuffix mat-icon-button aria-label="Clear" (click)="variable.size.model = ''" [disabled]="variable.size.disabled">
                        <mat-icon>close</mat-icon>
                    </button>
                </mat-form-field>
            </div>
            <div class="col" *ngIf="Variableopen">
                <button class="btn btn-danger float-end" (click)="removeVariable(i)">Remove</button>
            </div>
        </div>
        <div class="row" *ngIf="Variableopen">
            <div class="col">    
                <button class="btn btn-secondary" mat-button (click)="addLines()" [hidden]="!visible">Add</button>
                <p></p>
            </div>
        </div>
        <div class="row">
            <div class="col">
                <button class="btn btn-danger btn-lg" mat-raised-button (click)="clearFields()">Clear Vaiables</button>
            </div>
            <div class="col">
                <button class="btn btn-success btn-lg float-end btn-save">Save</button>
            </div>
        </div>
    </div>
</div>
<router-outlet></router-outlet>
