import { Component, Input } from '@angular/core';
import { DataService, IBoardObject } from '../data.service';
import { HomeComponent } from '../home/home.component';
import { MatDialog, MatDialogRef } from '@angular/material/dialog';


@Component({
  selector: 'app-new-board',
  templateUrl: './new-board.component.html',
  styleUrls: ['./new-board.component.css']
})
export class NewBoardComponent {
  @Input() Object: string[] = [];
  @Input() Drawing: string[] = [];
  @Input() Update: string[] = [];
  @Input() Hardware: string[] = [];
  @Input() CommentBoard: string[] = [];

  constructor(
    public dialogRef: MatDialogRef<NewBoardComponent>,
    public data: DataService
  ){

  }


  openBoard(){
    let newBoard: IBoardObject = {
      boardName: {
        label: 'Board Name',
        model: '',
        placeholder: '',
        disabled: false
      },
      drawNumber: {
        label: 'Drawing Number',
        model: '',
        placeholder: '',
        disabled: false
      },
      updateRate: {
        label: 'Update Rate',
        model: '',
        placeholder: '',
        disabled: false
      },
      hardware: {
        label: 'Hardware Version',
        model: '',
        placeholder: '',
        disabled: false
      },
      comment: {
        label: 'Comment',
        model: '',
        placeholder: '',
        disabled: false
      },
      variables: []
    }

    this.data.data?.boards.push(newBoard)
    this.dialogRef.close();
  }
}
