import { Component, Input } from '@angular/core';
import { SearchDialogComponent } from '../search-dialog/search-dialog.component';
import { MatDialog } from '@angular/material/dialog'
import { NewBoardComponent } from '../new-board/new-board.component';
import { MatMenu } from '@angular/material/menu';

export interface ProjectObject{
  username:{
    label: string,
    model: string,
    placeholder: string
  }
  Job: {
    label: string,
    model: string,
    placeholder: string
  }
  Manufacturer: {
    label: string,
    model: string,
    placeholder: string
  }
  Aircraft: {
    label: string,
    model: string,
    placeholder: string
  }
}

export interface BoardObject{
  boardName:{
    label: string,
    model: string,
    placeholder: string
  }
  drawNumber: {
    label: string,
    model: string,
    placeholder: string
  }
  updateRate: {
    label: string,
    model: string,
    placeholder: string
  }
  hardware: {
    label: string,
    model: string,
    placeholder: string
  }
  comment: {
    label: string,
    model: string,
    placeholder: string
  }
}

export interface VariableObject {
  objectName:{ 
    label: string,
     model: string,
      placeholder: string 
  },
  drawingNumber:{
    label: string,
     model: string,
      placeholder: string 
  },
  direction:{
    label:string;
    model: any;
  },
  dataType:{ 
    label: string,
     model: string,
      placeholder: string 
  },
  size:{ 
    label: string,
     model: string,
      placeholder: string 
  },
}

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],

})
export class HomeComponent {
  @Input() name = "";
  @Input() Job= "";
  @Input() Manufacturer= "";
  @Input() Aircraft= "";
  variableFields: VariableObject[] = [
    {
      objectName:{ 
        label: 'Object Name',
        model: '',
        placeholder: '' 
      },
      drawingNumber:{
         label: 'Drawing Number', 
         model: '', 
         placeholder: '' 
      },
      direction:{
        label: 'Direction',
        model: ''
      },
      dataType:{ 
        label: 'Data Type', 
        model: '', 
        placeholder: '' 
      },
      size:{ 
        label: 'Size', 
        model: '', 
        placeholder: '' 
      }
    }
  ];
  projectFields: ProjectObject[] = [
    {
    username:{
      label: 'Username',
      model: '',
      placeholder: ''
    },
    Job: {
      label: 'Job #',
      model: '',
      placeholder: ''
    },
    Manufacturer: {
      label: 'Manufaturer',
      model: '',
      placeholder: ''
    },
    Aircraft: {
      label: 'Aircraft Name',
      model: '',
      placeholder: ''
    }
}]
boardFields: BoardObject[] = [
  {
  boardName:{
    label: 'Board Name',
    model: '',
    placeholder: ''
  },
  drawNumber: {
    label: 'Drawing Number',
    model: '',
    placeholder: ''
  },
  updateRate: {
    label: 'Update Rate',
    model: '',
    placeholder: ''
  },
  hardware: {
    label: 'Hardware Version',
    model: '',
    placeholder: ''
  },
  comment: {
    label: 'Comment',
    model: '',
    placeholder: ''
  }
}]
isButtonVisible: boolean = true;

hide(): void {
  this.isButtonVisible = false;
  }
  constructor(public dialog: MatDialog) {}
  shouldRun: boolean  = true;
  isSidenavOpened: boolean = false;
  toggleSidenav(): void {
    this.isSidenavOpened = !this.isSidenavOpened;
  }
  openDialog(): void {

    const dialogRef = this.dialog.open(SearchDialogComponent,
      {
        width: '900px'
      });
    dialogRef.afterClosed().subscribe((result: any) =>{
    })
  }
  openAddNewBoard(): void {

    const dialogRef = this.dialog.open(NewBoardComponent,
      {
        width: '900px'
      });
    dialogRef.afterClosed().subscribe((result: any) =>{
    })
  }
  createBoard(): void{
    this.boardFields.push({
      boardName:{
        label: 'Board Name',
        model: '',
        placeholder: ''
      },
      drawNumber: {
        label: 'Drawing Number',
        model: '',
        placeholder: ''
      },
      updateRate: {
        label: 'Update Rate',
        model: '',
        placeholder: ''
      },
      hardware: {
        label: 'Hardware Version',
        model: '',
        placeholder: ''
      },
      comment: {
        label: 'Comment',
        model: '',
        placeholder: ''
      }
    })
    this.isButtonVisible = false;
  }
  createVariable(): void{
    this.variableFields.push({
      objectName:{ 
        label: 'Object Name',
         model: '',
          placeholder: '' 
      },
      drawingNumber:{
         label: 'Drawing Number', 
         model: '', 
         placeholder: '' 
      },
      direction:{
        label: 'Direction',
        model: ''
      },
      dataType:{ 
        label: 'Data Type', 
        model: '', 
        placeholder: '' 
      },
      size:{ 
        label: 'Size', 
        model: '', 
        placeholder: '' 
      }
    })
  }
  addLines(): void{
    this.variableFields.push({
      objectName:{ 
        label: 'Object Name',
         model: '',
          placeholder: '' 
      },
      drawingNumber:{
         label: 'Drawing Number', 
         model: '', 
         placeholder: '' 
      },
      direction:{
        label: 'Direction',
        model: ''
      },
      dataType:{ 
        label: 'Data Type', 
        model: '', 
        placeholder: '' 
      },
      size:{ 
        label: 'Size', 
        model: '', 
        placeholder: '' 
      }
    })
  }
    clearFields(): void {
      this.name = '';
      this.Job = '';
      this.Manufacturer = '';
      this.Aircraft = '';
      this.variableFields.forEach((field) => {
        field.objectName.model = '';
        field.drawingNumber.model = '';
        field.direction.model = null;
        field.dataType.model = '';
        field.size.model = '';
      });
    }
  }
